
var map;var newaIcon=new google.maps.MarkerImage('../gifs/newa_small.png',new google.maps.Size(16,16),new google.maps.Point(0,0),new google.maps.Point(8,8));var airportIcon=new google.maps.MarkerImage('../gifs/airport.png',new google.maps.Size(15,15),new google.maps.Point(0,0),new google.maps.Point(8,8));var culogIcon=new google.maps.MarkerImage('../gifs/culog.png',new google.maps.Size(14,14),new google.maps.Point(0,0),new google.maps.Point(7,7));var newaShadow=new google.maps.MarkerImage('../gifs/newa_small_shdw.png',new google.maps.Size(24,16),new google.maps.Point(0,0),new google.maps.Point(8,8));var airportShadow=new google.maps.MarkerImage('../gifs/airport_shdw.png',new google.maps.Size(23,15),new google.maps.Point(0,0),new google.maps.Point(8,8));var culogShadow=new google.maps.MarkerImage('../gifs/culog_shdw.png',new google.maps.Size(21,14),new google.maps.Point(0,0),new google.maps.Point(7,7));function placeMarkers(cur_data){var marker;var markerOptions={};var myLatlng=new google.maps.LatLng(42.95,-76.35);var mapOptions={zoom:7,center:myLatlng,mapTypeId:google.maps.MapTypeId.TERRAIN,mapTypeControl:true,mapTypeControlOptions:{style:google.maps.MapTypeControlStyle.DROPDOWN_MENU},streetViewControl:false,scaleControl:true};map=new google.maps.Map(document.getElementById("map"),mapOptions);markerOptions.map=map;$.each(cur_data.stations,function(i,stn){markerOptions.position=new google.maps.LatLng(stn.lat,stn.lon);markerOptions.title=stn.name;if(stn.network==="newa"||(stn.state==="MA"&&stn.network==="cu_log")){markerOptions.icon=newaIcon;markerOptions.shadow=newaShadow;}else if(stn.network==="cu_log"){markerOptions.icon=culogIcon;markerOptions.shadow=culogShadow;}else if(stn.network==="icao"){markerOptions.icon=airportIcon;markerOptions.shadow=airportShadow;}
marker=new google.maps.Marker(markerOptions);google.maps.event.addListener(marker,"click",function(){top.location.href="http://newa.cornell.edu/index.php?page=weather-station-page&WeatherStation="+stn.id;});});}
function zoomMarkCenter(results){var marker;var markerOptions={};var mapOptions={mapTypeId:google.maps.MapTypeId.SATELLITE,mapTypeControl:true,streetViewControl:false,mapTypeControlOptions:{style:google.maps.MapTypeControlStyle.DROPDOWN_MENU}};var maxZoomService=new google.maps.MaxZoomService();mapOptions.center=new google.maps.LatLng(results.metadata.lat,results.metadata.lon);maxZoomService.getMaxZoomAtLatLng(mapOptions.center,function(response){mapOptions.zoom=(response&&response.status===google.maps.MaxZoomStatus.OK)?Math.min(response.zoom,16):12;map=new google.maps.Map(document.getElementById("map"),mapOptions);markerOptions.map=map;markerOptions.position=mapOptions.center;marker=new google.maps.Marker(markerOptions);});}
function stationMap(){$.getJSON("/newaUtil/stationList/all").success(placeMarkers).error(function(){$('<div id="msg" style="border:1px solid black; padding:0.25em; position:absolute; left:168px; bottom:0px; width:225px; z-index:1; font-size:0.9em; text-align:center; background-color:red; color:white;"></div>').appendTo($("#map"));$("#msg").text('Error retrieving station list');});}
function zoomStation(reqstn){$.getJSON("/newaUtil/stationInfo/"+reqstn).success(zoomMarkCenter).error(function(){return false;});}